        &= \boldsymbol W\left(\frac{1}{k + 1}\mu\left(\mathcal{F}_{y_i}\right)+\frac{k}{k + 1}\mathbb{E}_{P\sim\mathcal{D}_P,c\sim B\left(y_i,p\right),\boldsymbol x_j \sim\mathcal{F}_c}\left[\boldsymbol x_j\right]\right).\\ 

    \end{aligned}

\end{equation*}

\begin{equation*}

		\begin{aligned}

			&\mathbb P\left[\frac{1}{k + 1}\left\|\sum_{j\in\{i\}\cup \mathcal{N}\left(i\right)}\left(\boldsymbol x_j^l-\mathbb E\left[\boldsymbol x_j^l\right]\right)\right\|\geq t_1\right]\\

			\leq&\mathbb P\left[\frac{1}{k}\left\|\sum_{j\in\mathcal{N}\left(i\right)}\left(\boldsymbol x_j^l-\mathbb E\left[\boldsymbol x_j^l\right]\right)\right\|\geq \frac{(k+1)t_1+C_{\boldsymbol{x}}+C_{\mu}}{k}\right]\\

			&+\mathbb P\left[\left\|\boldsymbol x_i^l-\mathbb E\left[\boldsymbol x_i^l\right]\right\|\geq C_{\boldsymbol{x}}+C_{\mu}\right]\\

			\leq&2 \exp \left(-\frac{((k+1)t_1+C_{\boldsymbol{x}}+C_{\mu})^2}{2k\sigma^2 + 4C_{\boldsymbol  x}((k+1)t_1+C_{\boldsymbol{x}}+C_{\mu})/3} \right).

		\end{aligned}

		\label{eq:bern}

	\end{equation*}

\begin{equation*}

		\begin{aligned}

			&\mathbb{P} \left( \frac{1}{k + 1}\left\|\sum_{j\in \left\{i\right\}\cup\mathcal{N}\left(i\right)}\left(\boldsymbol x_j-\mathbb{E}\left[\boldsymbol x_j\right]\right)\right\|_2\geq t_2 \right)\\

			\leq&\sum_{l=1}^{d}\mathbb P\left[\frac{1}{k + 1}\left\|\sum_{j\in\{i\}\cup \mathcal{N}\left(i\right)}\left(\boldsymbol x_j^l-\mathbb E\left[\boldsymbol x_j^l\right]\right)\right\|\geq\frac{t_2}{\sqrt{d}}\right]\\

			\leq&2d\exp \left(-\frac{((k+1)t_2+\sqrt{d}C_{\boldsymbol{x}}+\sqrt{d}C_{\mu})^2}{2kd\sigma^2 + 4\sqrt{d}C_{\boldsymbol  x}((k+1)t_2+\sqrt{d}C_{\boldsymbol{x}}+\sqrt{d}C_{\mu})/3} \right).

		\end{aligned}

		\label{eq:bound}

	\end{equation*}

\begin{equation*}

		\begin{aligned}

			\left\|\mathbf{h}_i-\mathbb{E}\left[\mathbf{h}_i\right]\right\|_2 & \leq  \frac{1}{k + 1}\left\|\boldsymbol W\left(\sum_{j\in\left\{i\right\}\cup \mathcal{N}\left(i\right)}\boldsymbol x_j-\mathbb{E}\left[\boldsymbol x_j\right]\right)\right\|_2\\ 

			&\leq \frac{\left\|\boldsymbol W\right\|_2}{k+1}\left\|\sum_{j\in\left\{i\right\}\cup \mathcal{N}\left(i\right)}\boldsymbol x_j-\mathbb{E}\left[\boldsymbol x_j\right]\right\|_2\\ 

			&\leq \frac{\rho(\boldsymbol W)}{k+1}\left\|\sum_{j\in\left\{i\right\}\cup \mathcal{N}\left(i\right)}\boldsymbol x_j-\mathbb{E}\left[\boldsymbol x_j\right]\right\|_2,\\ 

		\end{aligned}

	\end{equation*}

\begin{equation*}

		\begin{aligned} 

			&\mathbb{P}\left(\left\|\mathbf{h}_i-\mathbb{E}\left[\mathbf{h}_i\right]\right\|_2  \geq  t\right) leq \mathbb{P}\left(\frac{\rho(\boldsymbol W)}{k+1}\left\|\sum_{j\in \left\{i\right\}\cup \mathcal{N}\left(i\right)}\boldsymbol x_j-\mathbb{E}\left[\boldsymbol x_j\right]\right\|_2\geq  t\right)\\ 

			&=\mathbb{P}\left(\frac{1}{k+1}\left\|\sum_{j\in \{i\}\cup \mathcal{N}(i)}\boldsymbol x_j-\mathbb{E}[\boldsymbol x_j]\right\|_2\geq  \frac{t}{\rho(\boldsymbol W)}\right)\\

			&\leq 2d\exp \left(-\frac{((k+1)t_2/\rho(\boldsymbol{W})+\sqrt{d}C_{\boldsymbol{x}}+\sqrt{d}C_{\mu})^2}{2kd\sigma^2 + 4\sqrt{d}C_{\boldsymbol  x}((k+1)t_2/\rho(\boldsymbol{W})+\sqrt{d}C_{\boldsymbol {x}}+\sqrt{d}C_{\mu})/3} \right),\\

		\end{aligned}

	\end{equation*}

